insert into XMVLDCN (ID_METAMODELO, ID_VALIDACION, DESCRIPCION, CONSULTA ) values( 0, 1, 'Atributos Obligatorios', 'select a.ID_OBJETO AS idObjeto, a.ID_MODELO as idModelo, a.ID_TIPO_METAMODELO as idTipoMetamodelo, a.ID_JERARQUIA as idJerarquia, a.NOMBRE as nombre, a.DESCRIPCION as descripcion, a.ID_OBJETO_PADRE as idObjetoPadre, a.ORDEN as orden, b.NOMBRE as nombrePadre, ''Requiere atributos'' as descripcionValidacion, ''danger'' as nivel from XMOBJTMDL a left join XMOBJTMDL b on b.ID_OBJETO = a.ID_OBJETO_PADRE where a.ID_MODELO = :idModelo and exists( select 1 FROM XMMDL e INNER JOIN XMATRBTMTMDL c on c.ID_METAMODELO = e.ID_METAMODELO AND c.ID_TIPO_METAMODELO = a.ID_TIPO_METAMODELO LEFT JOIN XMATRBTOBJT d on d.ID_OBJETO = a.ID_OBJETO and d.ID_ATRIBUTO_METAMODELO = c.ID_ATRIBUTO_METAMODELO WHERE e.ID_MODELO = a.ID_MODELO and c.ES_OBLIGATORIO = ''1'' and trim(coalesce( d.VALOR ,c.VALOR_DEFECTO, '''' )) = '''' )');
insert into XMVLDCN (ID_METAMODELO, ID_VALIDACION, DESCRIPCION, CONSULTA ) values( 0, 2, 'Nombres en lista no repetidos', 'select a.ID_OBJETO AS idObjeto, a.ID_MODELO as idModelo, a.ID_TIPO_METAMODELO as idTipoMetamodelo, a.ID_JERARQUIA as idJerarquia, a.NOMBRE as nombre, a.DESCRIPCION as descripcion, a.ID_OBJETO_PADRE as idObjetoPadre, a.ORDEN as orden, b.NOMBRE as nombrePadre, ''Objeto Repetido'' as descripcionValidacion, ''danger'' as nivel from XMOBJTMDL a left join XMOBJTMDL b on b.ID_OBJETO = a.ID_OBJETO_PADRE where a.ID_MODELO = :idModelo and exists( select 1 from XMOBJTMDL c where c.ID_OBJETO_PADRE = a.ID_OBJETO_PADRE and c.ID_OBJETO <> a.ID_OBJETO and c.ID_JERARQUIA = a.ID_JERARQUIA and c.NOMBRE = a.NOMBRE )');
